package Exercicis;

public class EX_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  
		
		  //Declarem les variables.
		  int X=5;
		  int Y=10;
		  double N=4.3;
		  double M=5.5;
		
		  
		//Realitzem totes les operacions que ens demana l'enunciat. 
	  	System.out.println("X="+X+" Y="+Y+" N="+N+" M="+M);
	  	System.out.println("X+Y="+(X+Y));
	  	System.out.println("X-Y="+(X-Y));
	  	System.out.println("X*Y="+(X*Y));
	  	System.out.println("X/Y="+(X/Y));
		System.out.println("X%Y="+(X%Y));
		System.out.println("N+M="+(N+M));
		System.out.println("N-M="+(N-M));
		System.out.println("N*M="+(N*M));
		System.out.println("N/M="+(N/M));
		System.out.println("N%M="+(N%M));
		System.out.println("X+N="+(X+N));
		System.out.println("Y/M="+(Y/M));
		System.out.println("Y%M="+(Y%M));
		System.out.println("Doble de X="+(X*2)+" Doble de Y="+(Y*2)+" El doble de N="+(N*2)+" El doble de M="+(M*2));
		System.out.println("Suma de X,Y,N,M="+(X+Y+N+M));
		System.out.println("Producto de X,Y,N,M="+(X*Y*N*M));
	}

}
